mapper.select:
select distinct load_addr_gu
from load_addr
order by load_addr_gu asc;

select distinct load_addr_dong
from load_addr
where load_addr_gu = #{loadAddrGu}
order by load_addr_dong asc;

select distinct load_addr_ro
from load_addr
where load_addr_gu = #{loadAddrGu}
  and load_addr_dong = #{loadAddrDong}
order by load_addr_ro asc;

select distinct load_addr_main_num
from load_addr
where load_addr_gu = #{loadAddrGu}
  and load_addr_dong = #{loadAddrDong}
  and load_addr_ro = #{loadAddrRo}
order by load_addr_main_num asc;

select distinct load_addr_sub_num
from load_addr
where load_addr_gu = #{loadAddrGu}
  and load_addr_dong = #{loadAddrDong}
  and load_addr_ro = #{loadAddrRo}
  and load_addr_main_num = #{loadAddrMainNum}
order by load_addr_sub_num asc;
